@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@700&family=Open+Sans:wght@400;600&display=swap');

:root {
  --font-body: 'Open Sans', sans-serif;
  --font-heading: 'Montserrat', sans-serif;
  --color-bg: #f5f7fa;
  --color-text: #333;
  --color-primary: #1565c0;
  --color-secondary: #42a5f5;
  --color-card-bg: #ffffff;
  --color-border: #e0e0e0;
  --color-nav-bg: var(--color-primary);
  --color-nav-text: #fff;
  --color-footer-bg: var(--color-primary);
  --color-bot-bg: #e3f2fd;
}

body {
  font-family: var(--font-body);
  line-height: 1.6;
  margin: 0;
  background: var(--color-bg);
  color: var(--color-text);
  transition: background-color 0.3s ease, color 0.3s ease;
}

body.dark {
  --color-bg: #121212;
  --color-text: #eee;
  --color-card-bg: #1e1e1e;
  --color-border: #333;
  --color-nav-bg: #1e1e1e;
  --color-footer-bg: #1e1e1e;
  --color-bot-bg: #263238;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}

header {
  background: var(--color-nav-bg);
  color: var(--color-nav-text);
}

header .container {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1.5rem 0;
}

header h1 {
  font-family: var(--font-heading);
  margin: 0;
}

nav {
  margin-top: 1rem;
}

nav a {
  margin: 0 0.5rem;
  color: var(--color-nav-text);
  text-decoration: none;
  font-weight: 600;
}

nav a:hover {
  text-decoration: underline;
}

nav button {
  margin-left: 1rem;
  padding: 0.4rem 0.8rem;
  border: none;
  border-radius: 4px;
  background: var(--color-secondary);
  color: #fff;
  cursor: pointer;
}

main#layout {
  padding: 2rem 0;
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-template-areas:
    "about chat"
    "info info";
  gap: 2rem;
}

#chat {
  grid-area: chat;
}

#about {
  grid-area: about;
}

#info {
  grid-area: info;
}

section {
  background: var(--color-card-bg);
  border: 1px solid var(--color-border);
  border-radius: 8px;
  padding: 1.5rem;
  margin-bottom: 2rem;
}

#chat.highlight {
  border-color: var(--color-secondary);
  box-shadow: 0 0 10px rgba(66, 165, 245, 0.3);
}

.chat-intro {
  font-weight: bold;
  margin-bottom: 1rem;
}

#chat-container {
  width: 100%;
}

#chat-log {
  border: 1px solid var(--color-border);
  padding: 1rem;
  height: 400px;
  overflow-y: auto;
  background-color: var(--color-card-bg);
  background-image: url('Gemini_Generated_Image.png');
  background-size: cover;
  background-position: center 45%;
  background-repeat: no-repeat;
  border-radius: 8px;
}

.message {
  padding: 0.5rem;
  border-radius: 4px;
  margin-bottom: 0.5rem;
}

.message.user {
  background: var(--color-secondary);
  color: #fff;
}

.message.bot {
  background: var(--color-bot-bg);
}

.message.sources {
  background: transparent;
  font-size: 0.8rem;
  color: var(--color-text);
}

#chat-form {
  display: flex;
  gap: 0.5rem;
  margin-top: 1rem;
}

#chat-input {
  flex: 1;
  padding: 0.5rem;
  border-radius: 4px;
  border: 1px solid var(--color-border);
}

#chat-form button {
  background: var(--color-primary);
  color: #fff;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  cursor: pointer;
}

#chat-form button:hover {
  background: var(--color-secondary);
}

#projects-list .project {
  margin-bottom: 15px;
}

#resume-frame {
  width: 100%;
  height: 600px;
  border: none;
  display: none;
}

#resume-frame.show {
  display: block;
}

footer {
  background: var(--color-footer-bg);
  color: #fff;
  text-align: center;
  padding: 1rem 0;
  margin-top: 40px;
}

@media (max-width: 800px) {
  main#layout {
    grid-template-columns: 1fr;
    grid-template-areas:
      "about"
      "chat"
      "info";
  }

  nav {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
  }

  nav button {
    margin-top: 0.5rem;
  }
}

document.addEventListener('DOMContentLoaded', () => {
  // dynamic project list
  const projects = [
    {
      title: 'AI Log Analysis Tool',
      description: 'Designed a tool that integrates the NSP workflow manager with large language models to parse and analyze network logs.'
    },
    {
      title: 'Intent-Based Service Deployment',
      description: 'Automated network service provisioning using Pydantic schemas and LLaMA models to interpret user intent.'
    }
  ];

  const list = document.getElementById('projects-list');
  projects.forEach(p => {
    const div = document.createElement('div');
    div.className = 'project';
    div.innerHTML = `<h3>${p.title}</h3><p>${p.description}</p>`;
    list.appendChild(div);
  });

  // theme toggle
  const toggle = document.getElementById('theme-toggle');
  const currentTheme = localStorage.getItem('theme');
  if (currentTheme === 'dark') {
    document.body.classList.add('dark');
  }

  toggle.addEventListener('click', () => {
    document.body.classList.toggle('dark');
    localStorage.setItem('theme', document.body.classList.contains('dark') ? 'dark' : 'light');
  });

  // resume toggle
  const resumeBtn = document.getElementById('resume-toggle');
  const resumeFrame = document.getElementById('resume-frame');
  resumeBtn.addEventListener('click', () => {
    resumeFrame.classList.toggle('show');
    resumeBtn.textContent = resumeFrame.classList.contains('show') ? 'Hide Resume' : 'Show Resume';
  });

  // set footer year
  document.getElementById('year').textContent = new Date().getFullYear();
});
